import React from 'react';
import './css/mainStyle.scss';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Card from './components/Card';

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            topStories:[]
        }
    }
    componentDidMount() {
        let self = this;
        var url = 'https://newsapi.org/v2/top-headlines?country=us&apiKey=k8587bd9c0f864ad2b046563d2362dc1a';
        var req = new Request(url);
        fetch(req)
            .then(response => response.json())
            .then(jsonBody => { console.log(jsonBody); return jsonBody; })
            .then(result => {
                console.log(result.articles);
                self.setState({topStories: result.articles},()=>{
                    console.log(self.state.topStories)
                })
            });
    }

    render() {
        return (
            <div className="app-layout">
                <Header/>

                <div className="container mb-5">
                    {
                        (this.state.topStories && self.state.topStories.length > 0)
                            ?
                                <Carousel topStories={this.state.topStories}/>
                            :null

                    }
                </div>

                <div className="container">
                    <div className="page-heading">
                        <h3>LATEST NEWS</h3>
                    </div>
                </div>
                <div className="container mt-3">
                    <div className="card-columns">
                        {
                            ((self)=>{
                                let cardArry = [];
                                if(self.state.topStories && self.state.topStories.length > 0){
                                    self.state.topStories.map((data,i)=>{
                                        cardArry.push(
                                            <Card story={data} key={i}/>
                                        )
                                    });
                                    return cardArry;
                                }else {
                                    // TODO
                                }
                            })(this)
                        }
                    </div>
                </div>

            </div>
        );
    }
}

export default App;
